package com.example.feign.controller;import com.example.feign.net.NetFromEurka;import org.springframework.beans.factory.annotation.Autowired;import org.springframework.web.bind.annotation.GetMapping;import org.springframework.web.bind.annotation.PathVariable;import org.springframework.web.bind.annotation.RestController;/** * @author 朱朝阳 * @date 2019/9/30 20:18 */@RestControllerpublic class GetFromEurka {    /**     * 面向接口编程 FeignClient的注解类会被纳入到IOC容器中     */    @Autowired    private NetFromEurka netFromEurka;    @GetMapping(value = "/provider/test1/{name}")    public String getMsgFromEurka(@PathVariable("name") String name) {        return netFromEurka.getTest("我是消费者："+this.getClass().getName());    }}