package com.example.feign.net;import com.example.feign.hystrix.NetHystrix;import org.springframework.cloud.openfeign.FeignClient;import org.springframework.web.bind.annotation.GetMapping;import org.springframework.web.bind.annotation.PathVariable;/** * 编写接口从 * @author 朱朝阳 * @date 2019/9/30 20:08 */@FeignClient(value = "provider-test",fallback = NetHystrix.class)public interface NetFromEurka {    @GetMapping(value = "/provider/test1/{name}")    String getTest(@PathVariable("name") String name);}