package com.example.provider.controller;import org.springframework.beans.factory.annotation.Value;import org.springframework.web.bind.annotation.*;/** * @author 朱朝阳 * @date 2019/9/30 19:50 */@RestController@RequestMapping("/provider")public class RestCont {    @Value("${server.port}")    private String port;    @GetMapping("/test1/{name}")    public String providerTest(@PathVariable String name) {        return "Provider：" + this.getClass().getName() + "，端口号为：" + port + "，被" + name + "调用了";    }}